s('display','block').siblings().css('display','none');    }  );    function getByClass(oParent,sClass,tagName){      var aEle=document.getElementsByTagName(tagName),        aResult=[],        re=new RegExp('\\b'+sClass+'\\b','i'),        i=0;      for(i=0;i<aEle.length;i++){        if (re.test(aEle[i].className)) {          aResult.push(aEle[i]);        }      }      return aResult;    }  // 左导航菜单  $(".navleft").mouseover(function(){    var navleftmain=document.getElementById('navleftmain');    var navleftlist=getByClass(navleftmain,'navleftlist','div');    var snavleft=getByClass(navleftmain,'snavleft','div');    for (var i = 0; i < navleftlist.length; i++) {      var _thistop=navleftlist[i].offsetTop;      snavleft[i].style.top=_thistop-2*81*i-40+'px';    };    });  // 回到顶部  var agotop=$(".a-gotop");  agotop.click(function(){      $('html,body').animate({scrollTop:0},'slow');    }  );</script>